var Query = require('../../lib/query'),
    should = require('should');

describe('query', function() {

  /**
   * MIN
   *
   * Adds a MIN select parameter to a sql statement
   */

  describe('.min()', function() {

    describe('with array', function() {

      // Lookup criteria
      var criteria = {
        where: {
          name: 'foo'
        },
        min: ['age']
      };

      it('should use the min aggregate option in the select statement', function() {
        var query = new Query({ name: { type: 'text' }}).find('test', criteria);
        query.query.should.eql('SELECT MIN(\"age\") AS \"age\" FROM test WHERE LOWER(\"name\") = $1');
      });
    });

    describe('with string', function() {

      // Lookup criteria
      var criteria = {
        where: {
          name: 'foo'
        },
        min: 'age'
      };

      it('should use the MIN aggregate option in the select statement', function() {
        var query = new Query({ name: { type: 'text' }}).find('test', criteria);
        query.query.should.eql('SELECT MIN(\"age\") AS \"age\" FROM test WHERE LOWER(\"name\") = $1');
      });
    });

  });
});
